/*
   Home 主页
*/
import React from 'react';
// import {Data} from '../config/data';

const wordProp = [
  'n.',
  'pron.',
  'adj.',
  'adv.',
  'v.',
  'vt.',
  'vi.',
  'num.',
  'art.',
  'prep.',
  'conj.',
  'interj.',
]

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      word: `disguise 
      英  [dɪs'gaɪz]   美  [dɪs'ɡaɪz]
     vt. 掩饰；假装；隐瞒
     n. 伪装；假装；用作伪装的东西`,
      data: '',
    };
  }

  changeMeaning(event) {
    this.setState({word: event.target.value});
  }

  returnMeaningObj(wordStr, prop) {
    const res = [];
    let idx = wordStr.indexOf(prop);
    while (idx !== -1) {
      res.push({
        prop,
        index: idx
      });
      idx = wordStr.indexOf(prop, idx + 1);
    }
    return res;
  }

  addWord() {
    const wordStr = this.state.word;
    let wordPropsPosArr = [];
    let wordMeaningArr = [];
    let word = '';
    const lastIndex = wordPropsPosArr.length-1;

    wordProp.forEach((p) => {
      wordPropsPosArr = wordPropsPosArr.concat(this.returnMeaningObj(wordStr, p));
    });
    wordPropsPosArr.sort((a, b) => {
      return a.index - b.index
    });
    console.log('wordPropsPosArr' ,wordPropsPosArr);

    wordPropsPosArr.forEach((item, index) => {
      if (index === 0) {
        word = wordStr.slice(0, item.index);
        wordMeaningArr.push({
          property: item.prop,
          desc: wordStr.slice(item.index, wordPropsPosArr[index+1].index).split('；')
        });
      } else if (index === lastIndex) {
        wordMeaningArr.push({
          property: item.prop,
          desc: wordStr.slice(item.index, lastIndex+1).split('；')
        });
      } else {
        wordMeaningArr.push({
          property: item.prop,
          desc: wordStr.slice(item.index, wordPropsPosArr[index+1].index).split('；')
        });
      }
    });
    console.log(wordMeaningArr);

    // {
    //   word:'clause',
    //   meaning: [
    //     {
    //       property: 'n',
    //       desc: ['条款', '从句']
    //     }
    //   ]
    // },
  }

  // <input value={this.state.word} placeholder='word' onChange={(e) => this.changeWord(e)}/>

  render() {
    return (
      <React.Fragment>
        <textarea placeholder='meaning' value={this.state.word} rows='10' onChange={(e) => this.changeMeaning(e)}/>
        <button onClick={() => this.addWord()}>ADD</button>
        {this.state.data}
      </React.Fragment>
    );
  }
}

export default Home;
