/*
   Home 主页
*/
import React from 'react';
// import {Data} from '../config/data';

const wordProp = [
  'n.',
  'pron.',
  'adj.',
  'adv.',
  'v.',
  'vt.',
  'vi.',
  'num.',
  'art.',
  'prep.',
  'conj.',
  'interj.',
]

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      word: `one 
英  [wʌn]   美  [wʌn]
pron. 一个人；任何人
adj. 一的；唯一的
n. 一
num. 一；一个
n. (One)人名；(老)温`,
      data: '',
    };
  }

  changeMeaning(event) {
    this.setState({word: event.target.value});
  }

  returnMeaningObj(wordStr, prop) {
    const res = [];
    let idx = wordStr.indexOf(prop);
    while (idx !== -1) {
      res.push({
        prop,
        index: idx
      });
      idx = wordStr.indexOf(prop, idx + 1);
    }
    return res;
  }

  addWord() {
    const wordStr = this.state.word;
    let wordPropsPosArr = [];
    let wordMeaningArr = [];
    let word = '';
    const lastIndex = wordPropsPosArr.length-1;

    wordProp.forEach((p) => {
      wordPropsPosArr = wordPropsPosArr.concat(this.returnMeaningObj(wordStr, p));
    });
    wordPropsPosArr.sort((a, b) => {
      return a.index - b.index
    });
    console.log('wordPropsPosArr' ,wordPropsPosArr);

    // wordPropsPosArr.forEach((item, index) => {
    //   if (index === 0) {
    //     word = wordStr.slice(0, item.index);
    //     wordMeaningArr.push({
    //       property: item.prop,
    //       desc: wordStr.slice(item.index, wordPropsPosArr[index+1].index).split('；')
    //     });
    //   } else if (index === lastIndex) {
    //     wordMeaningArr.push({
    //       property: item.prop,
    //       desc: wordStr.slice(item.index, lastIndex+1).split('；')
    //     });
    //   } else {
    //     wordMeaningArr.push({
    //       property: item.prop,
    //       desc: wordStr.slice(item.index, wordPropsPosArr[index+1].index).split('；')
    //     });
    //   }
    // });

    // returnMeaningObj(wordStr, prop) {
    //   const res = [];
    //   let idx = wordStr.indexOf(prop);

    // 0, index1, index2, length
    // word: 0-index1 
    // meaning1: index1 - index2
    // meaning2: index2 - length
    let c = 0;
    while (c < wordPropsPosArr.length) {
      const target = wordPropsPosArr[c];
      const nextTarget = wordPropsPosArr[c+1];
      if (c===0) {
        word = wordStr.slice(0, target.index);
        wordMeaningArr.push({
          property: target.prop,
          desc: wordStr.slice(target.index + target.prop.length, nextTarget.index).split('；')
        });
      } else if(!nextTarget) {
        wordMeaningArr.push({
          property: target.prop,
          desc: wordStr.slice(target.index + target.prop.length).split('；')
        });
      } else {
        const nextTarget = wordPropsPosArr[c+1];
        wordMeaningArr.push({
          property: target.prop,
          desc: wordStr.slice(target.index + target.prop.length, nextTarget.index).split('；')
        });
      } 
      c++;
    }
    // }
    console.log('word', word);

    console.log('wordMeaningArr', wordMeaningArr);

    // {
    //   word:'clause',
    //   meaning: [
    //     {
    //       property: 'n',
    //       desc: ['条款', '从句']
    //     }
    //   ]
    // },
  }

  // <input value={this.state.word} placeholder='word' onChange={(e) => this.changeWord(e)}/>

  render() {
    return (
      <React.Fragment>
        <textarea placeholder='meaning' value={this.state.word} rows='10' onChange={(e) => this.changeMeaning(e)}/>
        <button onClick={() => this.addWord()}>ADD</button>
        {this.state.data}
      </React.Fragment>
    );
  }
}

export default Home;
